static FATFS S_Fatfs;				/* File system object */

//-------------------------------------------------------------------------------------------
void FatfsInit(void)
{
	f_mount(&S_Fatfs, "0:", 1);
}


void testdat(void)
{
	static FIL file;
	unsigned int bw = 0;
	FRESULT res;
	
	res = f_open(&file, "0:/xsx.txt", FA_OPEN_ALWAYS | FA_READ | FA_WRITE);
	
	res = f_write(&file, "12345678901234567890", 20, &bw);
	
	res = f_lseek(&file, 10);
	
	res = f_write(&file, "abc", 3, &bw);
	f_close(&file);
}
